package test3;

public class Java_Data_Types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Num = 10;
		String website = "google.com";
		char letter = 'M';
		double digit = 1.0;
		boolean test = true;
		
		System.out.println(Num + " is the number stored in Num");
		System.out.println("Website is " + website);
		System.out.println("Stored character is " + letter);
		System.out.println("Stored digit is " + digit);
		System.out.println("Stored boolean is "+ test);

	}

}
